package TypesOfVariables;

public class Registration {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String name="Shekar"; // declartion and  initialization
		String company="Ayubowan Kiwiland";
		String email="duleekalakm@gmail.com";
		
		int num1;
		num1=10;
		
		System.out.println("Value of variable name is :" + name);
		System.out.println("Value of variable company is :" + company);
		System.out.println("Value of variable email is :" + email);
		
		// Value of variables can change during execution of program
		
		name="Duleeka"; // declartion and  initialization
		company="INO";
		email="duleekalakmxxxxxxxxxxxx@hotmail.com";
		
		
		System.out.println("Value of variable name is :" + name);
		System.out.println("Value of variable company is :" + company);
		System.out.println("Value of variable email is :" + email);
		//System.out.println(num1);
	}


		
	
	}

